mapper.select:

select 

from project_device
where project_id = #{projectId,jdbcType=VARCHAR}
  and device_id = #{deviceId,jdbcType=VARCHAR}

mapper.delete:

delete from project_device
where project_id = #{projectId,jdbcType=VARCHAR}
  and device_id = #{deviceId,jdbcType=VARCHAR}

mapper.insert:

insert into project_device (project_id, device_id, `type`, 
  `state`, device_manager_id, checkin_date, 
  total_use_time, last_verify_date, return_date
  )
values (#{projectId,jdbcType=VARCHAR}, #{deviceId,jdbcType=VARCHAR}, #{type,jdbcType=VARCHAR}, 
  #{state,jdbcType=INTEGER}, #{deviceManagerId,jdbcType=VARCHAR}, #{checkinDate,jdbcType=DATE}, 
  #{totalUseTime,jdbcType=INTEGER}, #{lastVerifyDate,jdbcType=DATE}, #{returnDate,jdbcType=DATE}
  )

mapper.update:

update project_device
set `type` = #{type,jdbcType=VARCHAR},
  `state` = #{state,jdbcType=INTEGER},
  device_manager_id = #{deviceManagerId,jdbcType=VARCHAR},
  checkin_date = #{checkinDate,jdbcType=DATE},
  total_use_time = #{totalUseTime,jdbcType=INTEGER},
  last_verify_date = #{lastVerifyDate,jdbcType=DATE},
  return_date = #{returnDate,jdbcType=DATE}
where project_id = #{projectId,jdbcType=VARCHAR}
  and device_id = #{deviceId,jdbcType=VARCHAR}
