mapper.select:

select 

from project_member
where project_id = #{projectId,jdbcType=VARCHAR}
  and employee_id = #{employeeId,jdbcType=VARCHAR}

mapper.delete:

delete from project_member
where project_id = #{projectId,jdbcType=VARCHAR}
  and employee_id = #{employeeId,jdbcType=VARCHAR}

mapper.insert:

insert into project_member (project_id, employee_id, boss_in_project_id, 
  `role`, access_git, access_file_system, 
  in_email_list, in_timesheet_module, fault_track
  )
values (#{projectId,jdbcType=VARCHAR}, #{employeeId,jdbcType=VARCHAR}, #{bossInProjectId,jdbcType=VARCHAR}, 
  #{role,jdbcType=VARCHAR}, #{accessGit,jdbcType=VARCHAR}, #{accessFileSystem,jdbcType=VARCHAR}, 
  #{inEmailList,jdbcType=INTEGER}, #{inTimesheetModule,jdbcType=INTEGER}, #{faultTrack,jdbcType=INTEGER}
  )

mapper.update:

update project_member
set boss_in_project_id = #{bossInProjectId,jdbcType=VARCHAR},
  `role` = #{role,jdbcType=VARCHAR},
  access_git = #{accessGit,jdbcType=VARCHAR},
  access_file_system = #{accessFileSystem,jdbcType=VARCHAR},
  in_email_list = #{inEmailList,jdbcType=INTEGER},
  in_timesheet_module = #{inTimesheetModule,jdbcType=INTEGER},
  fault_track = #{faultTrack,jdbcType=INTEGER}
where project_id = #{projectId,jdbcType=VARCHAR}
  and employee_id = #{employeeId,jdbcType=VARCHAR}
